#! /bin/bash
############################################################################
#   Written by Zhuang Li, Purdue University. Last modified at 2021-03-01   #
############################################################################

EMAN2_EXE='e2proc2d.py'

# Test if e2proc2d.py from eman2 is callable
if ! command -v $EMAN2_EXE &> /dev/null
then
    echo "e2proc2d.py could not be found"
    exit
fi

# Preset variables
WORKING_DIR=$PWD
Thread=8
START_FLAG="start.png"
DEFAULT_VIEWER="eog"
GOOD_LIST="good.list"
BAD_LIST="bad.list"
BATCH_LIST='batch.list'

#Convert the mrc to png using multithread
cd $WORKING_DIR
echo -e "Will make change in \033[31mCURRENT FOLDER\033[0m, Are you sure about that??"
echo "If not, kill the job using Ctrl-C before countdown finish..."
echo "Delete list files to start a de novo check by:"
echo "rm "$GOOD_LIST" "$BAD_LIST" "$BATCH_LIST
echo "7" && sleep 0.5 && echo "6" && sleep 0.5
echo "5" && sleep 0.5 && echo "4" && sleep 0.5
echo "3" && sleep 0.5 && echo "2" && sleep 0.5
echo "1" && sleep 0.5

#Exclude previous checked files
if [ -e $GOOD_LIST ] && [ -e $BAD_LIST ]; then
	ls *.mrc | grep -v -f $GOOD_LIST | grep -v -f $BAD_LIST > $BATCH_LIST
	if [ $(cat $BATCH_LIST | wc -l) -eq 0 ];then
		echo "No New Images Imported to this Folder, Exiting..."
		exit -1
	else
		echo $(cat $BATCH_LIST | wc -l)" New Images Imported to this Folder..."
	fi
else
	ls *.mrc > $BATCH_LIST
fi

#Convert mrc files to png using eman2
cat $BATCH_LIST | xargs -n $Thread | while read lines
do
	for files in $lines
	do
	$EMAN2_EXE $files ${files%.*}.png --process filter.lowpass.gauss:cutoff_freq=0.05 --meanshrink 8 &
	done
	wait
done

#include minion.png into the target folder
base64 -d <<< "" > $START_FLAG

#Navigate the PNGs using System Picture Viewer
$DEFAULT_VIEWER $START_FLAG
if [ $? -eq 0 ];then
	echo "Image Navigation finished, will Generate Bad and Good list..."
else
	echo "The image Navigation was interupted, skipping the following step and exiting..."
	exit -1
fi

#comm -3 <(cat $BATCH_LIST|sed 's/.mrc$//g'|sort) <(ls *.png|sed 's/.png$//g'|sort) | sed 's/$/.mrc/g' >> $BAD_LIST
#Creat a good list and bad list
ls *.png | grep -v $START_FLAG | sed 's/.png$/.mrc/g' >> $GOOD_LIST
cat $BATCH_LIST | grep -v -f $GOOD_LIST >>$BAD_LIST
/bin/rm *.png
echo "Three files, batch.list good.list bad.list, are generated..."
echo "One can use the bad.list file to exclude bad images from star files..."
echo "Such as grep -v -f path/to/bad.list  particles.star > good.star"
